<script>
    jQuery(function() {
        function sideView(windowSize) {
            if (windowSize > 1000) {
                $('.side').show();
            } else {
                $('.side').hide();
            }
        }
        $(window).on('resize', function() {
            var windowSize = $(window).width();
            sideView(windowSize);
        })
        $('#menu-btn-check').on('click', function() {
            $('.side').toggle();
        });
    });
</script>
